mapper.select:
SELECT * FROM USERINFO
	WHERE USER_ID = #{userId}
	  AND PASSWORD = #{password}

mapper.insert:
INSERT INTO USERINFO
	(
	  USER_ID
	, PASSWORD
	, PHONE
	, DELETED_YN
	, E_MAIL
	) VALUES  
	(
	 #{userId, jdbcType=VARCHAR}
	,#{password, jdbcType=VARCHAR}
	,#{phone, jdbcType=VARCHAR}
	,'N'
	, #{userId, jdbcType=VARCHAR}
	)

mapper.select:
SELECT COUNT(*) CNT
  FROM USERINFO 
 WHERE USER_ID = #{userId}

mapper.update:
UPDATE USER
	SET PHONE = #{phone}
	AND USER_NAME = #{userName}
WHERE USER_ID = #{userId}
